import React, { useEffect, useState } from 'react'
import Cart from './Cart';

function Card(props) {

  const [qty, setQty] = useState(props.item.qty)

  const [ID, setID] = useState({})  
  const [cartBtn, setCartBtn] = useState('Add to Cart')
  // const [disable, setDisable] = useState(()=>{
  //   const disableStatus = localStorage.getItem(`disable-${props.item.id}`);
  //   return disableStatus === 'true';
  // })

  const HandleCart = ()=>{
    props.addToCart({
      name:props.item.name, 
      price: props.item.price, 
      qty, 
      imageUrl:props.item.imageUrl
    })
    setCartBtn('Added')
    // setDisable(true)
    // setID(prevIDs=>[...prevIDs,props.item.id])
    // const ids = JSON.stringify(ID)
    // localStorage.removeItem('array');
    // localStorage.setItem('array', ids);
  }


  return (
    <div className='card-item'>
      <div className='card-image'>
        <img className='image' src={props.item.imageUrl} alt='Image'/>
      </div>
      <div className='card-content'>
        <h3>{props.item.name}</h3>
        <h5>Rs. {props.item.price}</h5>
      </div>
      <div className='bottom-card'>
        <div className='Quantity'>
          <p>Qty : </p>
          <input type="number" className="qty" id='qty' min={1} value={qty} onChange={(e)=>{setQty(e.target.value)}}/>
        </div>
        <div className='cart-btn'>
          <button id={`btn-${props.itemNo}`} onClick={HandleCart}  >{cartBtn}</button>
        </div>
      </div>
    </div>
  )
}

export default Card
