import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Card from './Card';

function Cart() {
  const [cartItm, setCartItm] = useState([])

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await axios.get('http://localhost:5000/Cart');
        setCartItm(response.data);
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };
    
    fetchCartItems();
  }, []);

  const handleRemove = async (itemId) => {
    try {
      const response = await axios.delete(`http://localhost:5000/Cart/${itemId}`);
      if (response.data.success) {
        // Remove the item from the cartItm state
        setCartItm(prevCart => prevCart.filter(item => item.id !== itemId));
      }
    } catch (error) {
      console.error('Error removing item:', error);
    }
  };

  return (
    <div className='cart'>
      {cartItm.length > 0 ? (
        cartItm.map((item, index) => (
          <div key={item.id}>
            <Card item={item} />
            <button onClick={() => handleRemove(item.id)}>Delete</button>
          </div>
        ))
      ) : (
        <p>Cart is empty</p>
      )}
    </div>
  );
}

export default Cart;
