import axios from 'axios';
import React, { useEffect, useState } from 'react'

function Checkout(props) {
  const [cartItems, setCartItems] = useState([]);
  // const [reloadFlag, setReloadFlag] = useState(false);

  useEffect(() => {
    // window.location.reload()
    async function fetchCartItems() {
      try {
        const response = await axios.get('http://localhost:5000/cart');
        setCartItems(response.data);
      } catch (error) {
        console.log("Error fetching cart items: " + error);
      }
    }
    fetchCartItems();
  }, [props.reloadFlag]);

  return (
    <div className='checkout-container'>
      <h2>Checkout</h2>
      <div className='checkout-details'>
        <div className='order-summary'>
          <h3>Order Summary</h3>
          <ul>
            {cartItems.map((item, index) => (
              <li key={index}>
                {item.name} - Qty: {item.qty}
              </li>
            ))}
          </ul>
          <div className='total'>
            <p>Total Price: Rs. {totalPrice.toFixed(2)}</p>
            <p>GST (18%): Rs. {gst.toFixed(2)}</p>
            <p>Delivery Fee: Rs. {deliveryFee.toFixed(2)}</p>
            <p>Total Amount: Rs. {(totalPrice + gst + deliveryFee).toFixed(2)}</p>
          </div>
        </div>
        <div className='checkout-form'>
          <h3>Enter Your Details</h3>
          <input type='text' placeholder='Name' value={name} onChange={(e) => setName(e.target.value)} />
          <input type='email' placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type='tel' placeholder='Phone' value={phone} onChange={(e) => setPhone(e.target.value)} />
          <textarea placeholder='Address' value={address} onChange={(e) => setAddress(e.target.value)}></textarea>
          <button>Place Order</button>
        </div>
      </div>
    </div>
  );
}

export default Checkout;
