import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Card from './Card';

function Cart() {

  const [cartItm, setCartItm] = useState([])
  const [InCart, setInCart] = useState(true)
  // const [id, setId] = useState(0)
  console.log("hey "); 
  useEffect(()=>{
    const cartCall = async() =>{
      try {
        const Response = await axios.get('http://localhost:5000/Cart')
        const data = Response.data
        data.map((items, index)=> items["id"] = index)
        console.log(data);
        setCartItm(data)
      } catch (error) {
        console.log("Error "+error);
      }
    } 
    cartCall()
  

  },[])

  useEffect(() => {
    const addTrashCanButtons = () => {
      const btns = document.getElementsByClassName(`cart-btn`);
      for (let i = 0; i < btns.length; i++) {
        const element = document.getElementById(`btn-${i}`)
        if(element){
          element.classList.add('active')
          btns[i].innerHTML=''
          const button = document.createElement('button');
          button.innerHTML = '<span>&#128465;</span>';
          button.onclick = () => handleRemove(cartItm[i].id);
          button.id = `btn-${i}`;
          btns[i].appendChild(button);
        }
      }
    };

    addTrashCanButtons();
    
  }, [cartItm]);


  const handleRemove = async (index) => {
    try {
      // Implement your remove logic here
      console.log('Removing item at index: ', index);
      const updtCart = cartItm.filter((item, ind)=> item.id !== index )
      console.log(updtCart);
      setCartItm(updtCart)
      
    } catch (error) {
      console.log('Error removing item: ', error);
    }
  };

  // if (!props.cartItems) {
  //   return <div>No items in the cart</div>;
  // }

  return (
    <div className='cart'>
      {cartItm && cartItm.length>0? (cartItm.map((item, index)=>(
        <div key={index}>
          <Card key={item.id} itemNo = {index} item={item[0]}/>
        </div>
      )
      )):(
        <p>Cart is empty</p>
      )}
      
    </div>
  )
}

export default Cart
