import React, { useEffect, useState } from 'react'
import Cart from './Cart';

function Card(props) {

  const [qty, setQty] = useState(1)
  // const [cartId, setCartId] = useState(0)
  const [AddStatus, setAddStatus] = useState(false)
  const [cartBtn, setCartBtn] = useState('Add to Cart')

  const HandleCart = ()=>{
    props.addToCart({name:props.item.name, price: props.item.price, qty, imageUrl:props.item.imageUrl})
    setCartBtn('Added')
    setAddStatus(true)
  }


  return (
    <div className='card-item'>
      <div className='card-image'>
        <img className='image' src={props.item.imageUrl} alt='Image'/>
      </div>
      <div className='card-content'>
        <h3>{props.item.name}</h3>
        <h5>Rs. {props.item.price}</h5>
      </div>
      <div className='bottom-card'>
        <div className='Quantity'>
          <p>Qty : </p>
          <input type="number" className="qty" id='qty' min={1} value={qty} onChange={(e)=>{setQty(e.target.value)}}/>
        </div>
        <div className='cart-btn'>
          <button id={`btn-${props.itemNo}`} onClick={HandleCart} disabled={AddStatus}>{cartBtn}</button>
        </div>
      </div>
    </div>
  )
}

export default Card
