import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Card from './Card';

function Cart() {

  const [cartItm, setCartItm] = useState([])
  console.log("hey ");
  useEffect(()=>{
    const cartCall = async() =>{
      try {
        const Response = await axios.get('http://localhost:5000/Cart')
        console.log(Response);
        
        const data = await Response.data
        console.log(data);
        setCartItm(data)
        console.log(cartItm);
      } catch (error) {
        console.log("Error "+error);
      }
    } 
    cartCall()
    


    const handleRemove = (event) =>{
      if(event.target.classList.contains('cart-btn')){
        alert("Hey")
      }
      document.addEventListener('click',handleRemove)
      
    }

  },[])

  useEffect(() => {
    const addTrashCanButtons = () => {
      const btns = document.getElementsByClassName('cart-btn');
      for (let i = 0; i < btns.length; i++) {
        document.getElementById(i).classList.add('active')
        btns[i].innerHTML ='<button onClick={handleRemove}><span> &#128465</span></button>';
      }
    };

    addTrashCanButtons();
    
  }, [cartItm]);


  // if (!props.cartItems) {
  //   return <div>No items in the cart</div>;
  // }

  return (
    <div className='cart'>
      {cartItm && cartItm.length>0? (cartItm.map((item, index)=>(
        <div key={index}>
        {/* <h1>{item.name}</h1>*/}
          <Card key={index} itemNo = {index} item={item[0]} />
        </div>
      ))):(
        <p>Cart is empty</p>
      )}
    </div>
  )
}

export default Cart
