import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../Checkout.css';


function Checkout(props) {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [gst, setGst] = useState(0);
  const [deliveryFee, setDeliveryFee] = useState(0);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');

  useEffect(() => {
    async function fetchCartItems() {
      try {
        const response = await axios.get('http://localhost:5000/cart');
        setCartItems(response.data);
      } catch (error) {
        console.log("Error fetching cart items: " + error);
      }
    }
    fetchCartItems();
  }, []);

  // Calculate total price
  useEffect(() => {
    let total = 0;
    cartItems.forEach(item => {
      total += item.qty * item.price;
    });
    setTotalPrice(total);
  }, [cartItems]);

  // Calculate GST
  useEffect(() => {
    const gstRate = 0.18; // Assuming GST rate is 18%
    const gstAmount = totalPrice * gstRate;
    setGst(gstAmount);
  }, [totalPrice]);

  // Calculate delivery fee (example)
  useEffect(() => {
    // Implement your delivery fee calculation logic here
    // Example: Set a fixed delivery fee of Rs. 50
    const deliveryFee = 50;
    setDeliveryFee(deliveryFee);
  }, []);

  return (
    <div className='checkout-container'>
      <h2>Checkout</h2>
      <div className='checkout-details'>
        <div className='order-summary'>
          <h3>Order Summary</h3>
          <ul>
            {cartItems.map((item, index) => (
              <li key={index}>
                {item.name} - Qty: {item.qty}
              </li>
            ))}
          </ul>
          <div className='total'>
            <p>Total Price: Rs. {totalPrice.toFixed(2)}</p>
            <p>GST (18%): Rs. {gst.toFixed(2)}</p>
            <p>Delivery Fee: Rs. {deliveryFee.toFixed(2)}</p>
            <p>Total Amount: Rs. {(totalPrice + gst + deliveryFee).toFixed(2)}</p>
          </div>
        </div>
        <div className='checkout-form'>
          <h3>Enter Your Details</h3>
          <input type='text' placeholder='Name' value={name} onChange={(e) => setName(e.target.value)} />
          <input type='email' placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type='tel' placeholder='Phone' value={phone} onChange={(e) => setPhone(e.target.value)} />
          <textarea placeholder='Address' value={address} onChange={(e) => setAddress(e.target.value)}></textarea>
          <button>Place Order</button>
        </div>
      </div>
    </div>
  );
}

export default Checkout;
